import React, { useState, useEffect } from 'react';
import './UserProfile.css';

const UserProfile = ({ token, onClose, onLogout }) => {
  const [activeTab, setActiveTab] = useState('profile');
  const [orders, setOrders] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [userDetails, setUserDetails] = useState({
    firstName: '',
    lastName: '',
    email: '',
    username: '',
    role: ''
  });

  useEffect(() => {
    const realToken = token || localStorage.getItem('token');
    if (!realToken) return;
    fetch('http://localhost:8000/api/user/profile', {
      headers: { 'Authorization': `Bearer ${realToken}` }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setUserDetails({
            firstName: data.user.firstName,
            lastName: data.user.lastName,
            email: data.user.email,
            username: data.user.username,
            role: data.user.role
          });
        }
      })
      .catch(err => console.error(err));
  }, [token]);

  useEffect(() => {
    if (activeTab === 'orders') fetchOrders();
  }, [activeTab]);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const fetchOrders = async () => {
    try {
      const headers = getAuthHeaders();
      const response = await fetch('http://localhost:8000/api/orders', { headers });
      if (response.ok) {
        const result = await response.json();
        if (result.success) setOrders(result.orders);
      }
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const handleUpdateProfile = async () => {
    try {
      const headers = getAuthHeaders();
      const response = await fetch(`http://localhost:8000/api/user/profile`, {
        method: 'PUT',
        headers,
        body: JSON.stringify(userDetails)
      });
      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          alert('Profile updated successfully!');
          setIsEditing(false);
        }
      } else {
        alert('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile');
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserDetails(prev => ({ ...prev, [name]: value }));
  };

  if (!token) return <div className="user-profile"><p>Please log in to view your profile.</p></div>;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="user-profile-modal" onClick={e => e.stopPropagation()}>
        <div className="user-profile-layout">
          <aside className="user-profile-sidebar">
            <div className="user-profile-avatar-block">
              <div className="avatar-circle">
                {userDetails.firstName?.charAt(0)}
                {userDetails.lastName?.charAt(0)}
              </div>
              <div className="user-profile-name">{userDetails.firstName} {userDetails.lastName}</div>
              <div className="user-profile-role">{userDetails.role === 'admin' ? 'Administrator' : 'Customer'}</div>
            </div>
            <nav className="user-profile-tabs">
              <button className="logout-btn" onClick={onLogout}>Sign Out</button>
              <button className="close-btn" onClick={onClose}>Ã—</button>
            </nav>
          </aside>
          <main className="user-profile-main">
            {activeTab === 'profile' && (
              <div className="profile-info-block">
                <h2>Profile Details</h2>
                <form className="profile-form" onSubmit={e => { e.preventDefault(); handleUpdateProfile(); }}>
                  <div className="profile-form-row">
                    <label>First Name</label>
                    <input type="text" name="firstName" value={userDetails.firstName} onChange={handleInputChange} disabled={!isEditing} />
                  </div>
                  <div className="profile-form-row">
                    <label>Last Name</label>
                    <input type="text" name="lastName" value={userDetails.lastName} onChange={handleInputChange} disabled={!isEditing} />
                  </div>
                  <div className="profile-form-row">
                    <label>Username</label>
                    <input type="text" name="username" value={userDetails.username} onChange={handleInputChange} disabled={!isEditing} />
                  </div>
                  <div className="profile-form-row">
                    <label>Email</label>
                    <input type="email" name="email" value={userDetails.email} onChange={handleInputChange} disabled={!isEditing} />
                  </div>
                  <div className="profile-form-actions">
                    {!isEditing ? (
                      <button type="button" className="edit-btn" onClick={() => setIsEditing(true)}>Edit</button>
                    ) : (
                      <>
                        <button type="button" className="cancel-btn" onClick={() => setIsEditing(false)}>Cancel</button>
                        <button type="submit" className="save-btn">Save</button>
                      </>
                    )}
                  </div>
                </form>
              </div>
            )}
            {/* Other tabs (orders, security, help, settings) remain unchanged */}
          </main>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
