import React from 'react';

const PersonalDetails = ({ user, shippingDetails, onProceed }) => {
  const [form, setForm] = React.useState({
    address: shippingDetails?.address || '',
    city: shippingDetails?.city || '',
    state: shippingDetails?.state || '',
    zipCode: shippingDetails?.zipCode || '',
    country: shippingDetails?.country || ''
  });
  const [submitted, setSubmitted] = React.useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmitted(true);
    if (form.address && form.city && form.state && form.zipCode && form.country) {
      onProceed(form);
    }
  };

  return (
    <div className="personal-details-container">
      <h2>Personal & Shipping Details</h2>
      <div className="details-section">
        <h3>User Info</h3>
        <p><strong>Name:</strong> {user?.firstName} {user?.lastName}</p>
        <p><strong>Email:</strong> {user?.email}</p>
      </div>
      <form className="details-section" onSubmit={handleSubmit}>
        <h3>Shipping Info</h3>
        <label>Address:<br/>
          <input name="address" value={form.address} onChange={handleChange} required />
        </label><br/>
        <label>City:<br/>
          <input name="city" value={form.city} onChange={handleChange} required />
        </label><br/>
        <label>State:<br/>
          <input name="state" value={form.state} onChange={handleChange} required />
        </label><br/>
        <label>Zip Code:<br/>
          <input name="zipCode" value={form.zipCode} onChange={handleChange} required />
        </label><br/>
        <label>Country:<br/>
          <input name="country" value={form.country} onChange={handleChange} required />
        </label><br/>
        <button className="proceed-btn" type="submit">
          Proceed to Payment
        </button>
        {submitted && !(form.address && form.city && form.state && form.zipCode && form.country) && (
          <div style={{color:'red'}}>Please fill all fields.</div>
        )}
      </form>
    </div>
  );
};

export default PersonalDetails;
