import React, { useEffect, useState } from 'react';
import './UserProfile.css';

const UserProfile = ({ token }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    if (!token) return;
    setLoading(true);
    fetch('/api/user/profile', {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) setUser(data.user);
        else setError(data.message || 'Failed to load user profile');
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to load user profile');
        setLoading(false);
      });
  }, [token]);

  if (!token) return <div className="user-profile"><p>Please log in to view your profile.</p></div>;
  if (loading) return <div className="user-profile"><p>Loading profile...</p></div>;
  if (error) return <div className="user-profile"><p style={{color:'#dc2626'}}>{error}</p></div>;

  return (
    <div className="user-profile">
      <h2>User Profile</h2>
      <div className="profile-details">
        <div><strong>Username:</strong> {user.username}</div>
        <div><strong>Email:</strong> {user.email}</div>
        <div><strong>First Name:</strong> {user.firstName}</div>
        <div><strong>Last Name:</strong> {user.lastName}</div>
        <div><strong>Role:</strong> {user.role}</div>
        <div><strong>Joined:</strong> {new Date(user.createdAt).toLocaleDateString()}</div>
      </div>
    </div>
  );
};

export default UserProfile;
