import React, { useState, useEffect } from 'react';
import './UserProfile.css';

const UserProfile = ({ user, token, onClose, onLogout }) => {
  const [userDetails, setUserDetails] = useState(null);
  const [isEditing, setIsEditing] = useState(false);
  const [activeTab, setActiveTab] = useState('profile');
  const [quote, setQuote] = useState('');

  useEffect(() => {
    if (!token) return;
    fetch('https://api.quotable.io/random')
      .then(res => res.json())
      .then(data => setQuote(data.content));
    fetch('http://localhost:8000/api/user/profile', {
      headers: { 'Authorization': `Bearer ${token}` }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) setUserDetails(data.user);
      });
  }, [token]);

  const handleInputChange = e => {
    const { name, value } = e.target;
    setUserDetails(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = async () => {
    const response = await fetch('http://localhost:8000/api/user/profile', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': `Bearer ${token}`
      },
      body: JSON.stringify(userDetails)
    });
    if (response.ok) {
      setIsEditing(false);
    }
  };

  if (!userDetails) return <div className="user-profile-modal"><div className="loader">Loading...</div></div>;

  return (
    <div className="user-profile-modal">
      <div className="profile-header">
        <div className="avatar-circle">
          {userDetails.firstName?.[0]}{userDetails.lastName?.[0]}
        </div>
        <div>
          <h2>{userDetails.firstName} {userDetails.lastName}</h2>
          <div className="user-role">{userDetails.role === 'admin' ? 'Administrator' : 'Customer'}</div>
        </div>
        <button className="close-btn" onClick={onClose}>×</button>
      </div>
      <div className="profile-tabs">
        <button className={activeTab === 'profile' ? 'active' : ''} onClick={() => setActiveTab('profile')}>Profile</button>
        <button className={activeTab === 'settings' ? 'active' : ''} onClick={() => setActiveTab('settings')}>Settings</button>
        <button className={activeTab === 'logout' ? 'active' : ''} onClick={onLogout}>Logout</button>
      </div>
      <div className="profile-content">
        {activeTab === 'profile' && (
          <div className="profile-details-card">
            <h3>Welcome, {userDetails.firstName}!</h3>
            <p className="profile-quote">“{quote}”</p>
            <div className="profile-details-grid">
              <div><span>Name:</span> {isEditing ? <input name="firstName" value={userDetails.firstName} onChange={handleInputChange} /> : userDetails.firstName}</div>
              <div><span>Surname:</span> {isEditing ? <input name="lastName" value={userDetails.lastName} onChange={handleInputChange} /> : userDetails.lastName}</div>
              <div><span>Username:</span> {isEditing ? <input name="username" value={userDetails.username} onChange={handleInputChange} /> : userDetails.username}</div>
              <div><span>Email:</span> {isEditing ? <input name="email" value={userDetails.email} onChange={handleInputChange} /> : userDetails.email}</div>
              <div><span>Role:</span> {userDetails.role}</div>
            </div>
            {!isEditing ? (
              <button className="edit-btn" onClick={() => setIsEditing(true)}>Edit Profile</button>
            ) : (
              <div className="edit-actions">
                <button className="save-btn" onClick={handleSave}>Save</button>
                <button className="cancel-btn" onClick={() => setIsEditing(false)}>Cancel</button>
              </div>
            )}
          </div>
        )}
        {activeTab === 'settings' && (
          <div className="settings-card">
            <h3>Account Settings</h3>
            <div className="setting-item">
              <label><input type="checkbox" defaultChecked /> Email Notifications</label>
            </div>
            <div className="setting-item">
              <label><input type="checkbox" /> Promotional Offers</label>
            </div>
            <div className="setting-item">
              <label><input type="checkbox" /> Dark Mode</label>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default UserProfile;
