import React, { useState, useEffect } from 'react';
import './UserProfile.css';

const UserProfile = ({ token, onClose, onLogout }) => {
  const [activeTab, setActiveTab] = useState('profile');
  const [orders, setOrders] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [userDetails, setUserDetails] = useState({
    firstName: '',
    lastName: '',
    email: '',
    username: ''
  });

  useEffect(() => {
    const realToken = token || localStorage.getItem('token');
    if (!realToken) {
      return;
    }
    fetch('http://localhost:8000/api/user/profile', {
      headers: { 'Authorization': `Bearer ${realToken}` }
    })
      .then(res => res.json())
      .then(data => {
        if (data.success) {
          setUserDetails({
            firstName: data.user.firstName,
            lastName: data.user.lastName,
            email: data.user.email,
            username: data.user.username
          });
        }
      })
      .catch(err => console.error(err));
  }, [token]);

  useEffect(() => {
    if (activeTab === 'orders') {
      fetchOrders();
    }
  }, [activeTab]);

  const getAuthHeaders = () => {
    const token = localStorage.getItem('token');
    return {
      'Content-Type': 'application/json',
      'Authorization': `Bearer ${token}`
    };
  };

  const fetchOrders = async () => {
    try {
      const headers = getAuthHeaders();
      const response = await fetch('http://localhost:8000/api/orders', { headers });
      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          setOrders(result.orders);
        }
      }
    } catch (error) {
      console.error('Error fetching orders:', error);
    }
  };

  const formatPrice = (price) => {
    return new Intl.NumberFormat('en-IN', {
      style: 'currency',
      currency: 'INR',
      minimumFractionDigits: 0,
      maximumFractionDigits: 0
    }).format(price);
  };

  const formatDate = (dateString) => {
    return new Date(dateString).toLocaleDateString('en-IN', {
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });
  };

  const handleUpdateProfile = async () => {
    try {
      const headers = getAuthHeaders();
      const response = await fetch(`http://localhost:8000/api/user/profile`, {
        method: 'PUT',
        headers,
        body: JSON.stringify(userDetails)
      });

      if (response.ok) {
        const result = await response.json();
        if (result.success) {
          alert('Profile updated successfully!');
          setIsEditing(false);
        }
      } else {
        alert('Failed to update profile');
      }
    } catch (error) {
      console.error('Error updating profile:', error);
      alert('Error updating profile');
    }
  };

  const getStatusBadgeClass = (status) => {
    switch (status) {
      case 'pending': return 'status-pending';
      case 'confirmed': return 'status-confirmed';
      case 'shipped': return 'status-shipped';
      case 'delivered': return 'status-delivered';
      case 'cancelled': return 'status-cancelled';
      default: return 'status-pending';
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setUserDetails(prev => ({ ...prev, [name]: value }));
  };

  if (!token) return <div className="user-profile"><p>Please log in to view your profile.</p></div>;

  return (
    <div className="modal-overlay" onClick={onClose}>
      <div className="user-profile-modal" onClick={(e) => e.stopPropagation()}>
        <div className="modal-header">
          <h2>My Account</h2>
          <button onClick={onClose} className="close-btn">×</button>
        </div>

        <div className="profile-content">
          <div className="profile-sidebar">
            <div className="user-avatar">
              <div className="avatar-circle">
                {userDetails.firstName.charAt(0)}{userDetails.lastName.charAt(0)}
              </div>
              <h3>{userDetails.firstName} {userDetails.lastName}</h3>
              <p className="user-role">{userDetails.role === 'admin' ? 'Administrator' : 'Customer'}</p>
            </div>

            <nav className="profile-nav">
              <button 
                className={`nav-item ${activeTab === 'profile' ? 'active' : ''}`}
                onClick={() => setActiveTab('profile')}
              >
                <span className="nav-icon">👤</span>
                Profile Details
              </button>
              <button 
                className={`nav-item ${activeTab === 'orders' ? 'active' : ''}`}
                onClick={() => setActiveTab('orders')}
              >
                <span className="nav-icon">📦</span>
                My Orders
              </button>
              <button 
                className={`nav-item ${activeTab === 'security' ? 'active' : ''}`}
                onClick={() => setActiveTab('security')}
              >
                <span className="nav-icon">🔒</span>
                Security
              </button>
              <button 
                className={`nav-item ${activeTab === 'help' ? 'active' : ''}`}
                onClick={() => setActiveTab('help')}
              >
                <span className="nav-icon">❓</span>
                Help Center
              </button>
              <button 
                className={`nav-item ${activeTab === 'settings' ? 'active' : ''}`}
                onClick={() => setActiveTab('settings')}
              >
                <span className="nav-icon">⚙️</span>
                Settings
              </button>
            </nav>
          </div>

          <div className="profile-main">
            {activeTab === 'profile' && (
              <div className="profile-info">
                <div className="profile-avatar">
                  <div className="avatar-circle">
                    {userDetails.firstName.charAt(0)}{userDetails.lastName.charAt(0)}
                  </div>
                  <div className="user-role">{userDetails.role || 'User'}</div>
                </div>
                
                <div className="profile-fields">
                  <div className="form-group">
                    <label>First Name</label>
                    <input
                      type="text"
                      name="firstName"
                      value={userDetails.firstName}
                      onChange={handleInputChange}
                      disabled={!isEditing}
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Last Name</label>
                    <input
                      type="text"
                      name="lastName"
                      value={userDetails.lastName}
                      onChange={handleInputChange}
                      disabled={!isEditing}
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Username</label>
                    <input
                      type="text"
                      name="username"
                      value={userDetails.username}
                      onChange={handleInputChange}
                      disabled={!isEditing}
                    />
                  </div>
                  
                  <div className="form-group">
                    <label>Email Address</label>
                    <input
                      type="email"
                      name="email"
                      value={userDetails.email}
                      onChange={handleInputChange}
                      disabled={!isEditing}
                    />
                  </div>
                  
                  <div className="profile-actions">
                    {!isEditing ? (
                      <button onClick={() => setIsEditing(true)} className="edit-btn">
                        Edit Profile
                      </button>
                    ) : (
                      <>
                        <button onClick={() => setIsEditing(false)} className="cancel-btn">
                          Cancel
                        </button>
                        <button onClick={handleUpdateProfile} className="save-btn">
                          Save Changes
                        </button>
                      </>
                    )}
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'orders' && (
              <div className="orders-tab">
                <div className="tab-header">
                  <h3>Order History</h3>
                  <span className="order-count">{orders.length} orders</span>
                </div>

                {orders.length === 0 ? (
                  <div className="empty-orders">
                    <div className="empty-icon">📦</div>
                    <h4>No orders yet</h4>
                    <p>Start shopping to see your orders here!</p>
                  </div>
                ) : (
                  <div className="orders-list">
                    {orders.map(order => (
                      <div key={order._id} className="order-card">
                        <div className="order-header">
                          <div className="order-info">
                            <h4>Order #{order._id.slice(-8)}</h4>
                            <p className="order-date">{formatDate(order.createdAt)}</p>
                          </div>
                          <div className="order-status">
                            <span className={`status-badge ${getStatusBadgeClass(order.status)}`}>
                              {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                            </span>
                          </div>
                        </div>
                        
                        <div className="order-items">
                          {order.items.map((item, index) => (
                            <div key={index} className="order-item">
                              <img src={item.image || 'https://via.placeholder.com/50x50?text=Product'} alt={item.name} />
                              <div className="item-details">
                                <span className="item-name">{item.name}</span>
                                <span className="item-quantity">Qty: {item.quantity}</span>
                              </div>
                              <span className="item-price">{formatPrice(item.price * item.quantity)}</span>
                            </div>
                          ))}
                        </div>
                        
                        <div className="order-footer">
                          <span className="order-total">Total: {formatPrice(order.total)}</span>
                          <button className="view-order-btn">View Details</button>
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            )}

            {activeTab === 'security' && (
              <div className="security-tab">
                <div className="tab-header">
                  <h3>Security Settings</h3>
                </div>

                <div className="security-cards">
                  <div className="security-card">
                    <h4>Password</h4>
                    <p>Last changed 3 months ago</p>
                    <button className="security-btn">Change Password</button>
                  </div>
                  
                  <div className="security-card">
                    <h4>Two-Factor Authentication</h4>
                    <p>Add an extra layer of security to your account</p>
                    <button className="security-btn">Enable 2FA</button>
                  </div>
                  
                  <div className="security-card">
                    <h4>Login Activity</h4>
                    <p>See where you're logged in and log out of other sessions</p>
                    <button className="security-btn">View Activity</button>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'help' && (
              <div className="help-tab">
                <div className="tab-header">
                  <h3>Help Center</h3>
                </div>

                <div className="help-cards">
                  <div className="help-card">
                    <div className="help-icon">📞</div>
                    <h4>Contact Support</h4>
                    <p>Get help from our customer service team</p>
                    <span className="help-link">support@electrostore.com</span>
                  </div>
                  
                  <div className="help-card">
                    <div className="help-icon">❓</div>
                    <h4>FAQ</h4>
                    <p>Find answers to commonly asked questions</p>
                    <button className="help-btn">View FAQ</button>
                  </div>
                  
                  <div className="help-card">
                    <div className="help-icon">🚚</div>
                    <h4>Shipping Info</h4>
                    <p>Learn about our shipping policies and delivery times</p>
                    <button className="help-btn">Shipping Details</button>
                  </div>
                  
                  <div className="help-card">
                    <div className="help-icon">🔄</div>
                    <h4>Returns & Refunds</h4>
                    <p>Information about returns and refund policies</p>
                    <button className="help-btn">Return Policy</button>
                  </div>
                </div>
              </div>
            )}

            {activeTab === 'settings' && (
              <div className="settings-tab">
                <div className="tab-header">
                  <h3>Account Settings</h3>
                </div>

                <div className="settings-cards">
                  <div className="settings-card">
                    <h4>Notifications</h4>
                    <div className="setting-item">
                      <label>
                        <input type="checkbox" defaultChecked />
                        Email notifications
                      </label>
                    </div>
                    <div className="setting-item">
                      <label>
                        <input type="checkbox" defaultChecked />
                        Order updates
                      </label>
                    </div>
                    <div className="setting-item">
                      <label>
                        <input type="checkbox" />
                        Promotional emails
                      </label>
                    </div>
                  </div>
                  
                  <div className="settings-card">
                    <h4>Privacy</h4>
                    <div className="setting-item">
                      <label>
                        <input type="checkbox" defaultChecked />
                        Show profile to other users
                      </label>
                    </div>
                    <div className="setting-item">
                      <label>
                        <input type="checkbox" />
                        Allow data collection for analytics
                      </label>
                    </div>
                  </div>
                  
                  <div className="settings-card danger-zone">
                    <h4>Danger Zone</h4>
                    <p>These actions cannot be undone</p>
                    <button className="danger-btn" onClick={onLogout}>
                      Sign Out
                    </button>
                    <button className="danger-btn">
                      Delete Account
                    </button>
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default UserProfile;
